from typing import List
from collections import Counter

class Solution:
    def maxOperations(self, nums: List[int], k: int) -> int:
        # Create a counter for the number of occurrences of each number in nums
        count = Counter(nums)
        operations = 0

        for num in count:
            # Find the complement of the current number that would sum up to k
            complement = k - num
            if complement in count:
                if num != complement:
                    # Possible operations are limited by the smaller count between num and complement
                    operations += min(count[num], count[complement])
                else:
                    # Special case for when num and complement are the same
                    # The number of operations possible is the integer division of num's count by 2
                    operations += count[num] // 2

        return operations
