class Solution:
    def findMinArrowShots(self, points: List[List[int]]) -> int:
        points.sort(key=lambda x: x[1])
        arrows = 0
        current_end = float('-inf')
        for xstart, xend in points:
            if xstart > current_end:
                arrows += 1
                current_end = xend
        return arrows

