class Solution:
    def removeStars(self, s: str) -> str:
        # We will use a stack to efficiently perform operations as described.
        stack = []

        # Iterate over each character in the string `s`.
        for char in s:
            if char == '*':
                # If the character is a star, pop the last character from the stack
                # which effectively removes the closest non-star character to the left.
                if stack:
                    stack.pop()
            else:
                # If the character is not a star, push it onto the stack.
                stack.append(char)

        # Convert the stack back to a string and return it as the result.
        return ''.join(stack)
