            if char in vowels:
                vowel_positions.append(i)
                vowel_chars.append(char)

        # Reverse the order of vowel characters
        vowel_chars.reverse()

        # Convert the string to a list to modify it
        s_list = list(s)

        # Place reversed vowels back into their original positions
        for pos, char in zip(vowel_positions, vowel_chars):
            s_list[pos] = char

        # Join list to form the resulting string
        return ''.join(s_list)
