class Solution:
    def minimumTotal(self, triangle: List[List[int]]) -> int:
        if not triangle:
            return 0

        # Start from the bottom of the triangle and move upwards
        # We'll use dynamic programming, altering the triangle in place for O(n) space complexity
        for row in range(len(triangle) - 2, -1, -1):
            for col in range(len(triangle[row])):
                triangle[row][col] += min(triangle[row + 1][col], triangle[row + 1][col + 1])

        # The top of the triangle will eventually hold the minimum path sum
        return triangle[0][0]
