        word_to_pattern = {}

        for p, w in zip(pattern, words):
            if p in pattern_to_word:
                if pattern_to_word[p] != w:
                    return False
            else:
                if w in word_to_pattern:
                    return False
                pattern_to_word[p] = w
                word_to_pattern[w] = p

        return True
