from typing import List
from collections import deque

class Solution:
    def canVisitAllRooms(self, rooms: List[List[int]]) -> bool:
        n = len(rooms)
        visited = [False] * n
        queue = deque([0])
        visited[0] = True
        visited_rooms_count = 1

        while queue:
            room = queue.popleft()
            for key in rooms[room]:
                if not visited[key]:
                    visited[key] = True
                    visited_rooms_count += 1
                    queue.append(key)

            if visited_rooms_count == n:
                return True

        return visited_rooms_count == n

