class Solution:
    def longestPalindrome(self, s: str) -> str:
        def expand_around_center(left: int, right: int) -> str:
            while left >= 0 and right < len(s) and s[left] == s[right]:
                left -= 1
                right += 1
            return s[left + 1:right]

        longest_palindrome = ""
        for i in range(len(s)):
            # Try to expand around a single character center
            palindrome_odd = expand_around_center(i, i)
            # Try to expand around a two character center
            palindrome_even = expand_around_center(i, i + 1)

            # Update longest palindrome found
            longest_palindrome = max(longest_palindrome, palindrome_odd, palindrome_even, key=len)

        return longest_palindrome

