from typing import List
from collections import Counter

class Solution:
    def findSubstring(self, s: str, words: List[str]) -> List[int]:
        if not s or not words:
            return []

        word_len = len(words[0])
        total_words_len = word_len * len(words)
        words_count = Counter(words)

        result_indices = []

        for i in range(word_len):
            start = i
            window_count = Counter()

            for j in range(i, len(s) - word_len + 1, word_len):
                current_word = s[j:j + word_len]
                if current_word in words_count:
                    window_count[current_word] += 1

                    while window_count[current_word] > words_count[current_word]:
                        start_word = s[start:start + word_len]
                        window_count[start_word] -= 1
                        start += word_len

                    if j + word_len - start == total_words_len:
                        result_indices.append(start)
                else:
                    window_count.clear()
                    start = j + word_len

        return result_indices
