class Solution:
    def minDistance(self, word1: str, word2: str) -> int:
        # Get lengths of both words
        m, n = len(word1), len(word2)

        # Create a 2D dp array to store minimum operations
        dp = [[0] * (n + 1) for _ in range(m + 1)]

        # Fill the first row and column
        for i in range(m + 1):
            dp[i][0] = i
        for j in range(n + 1):
            dp[0][j] = j

        # Calculate the minimum operations to convert word1 to word2
        for i in range(1, m + 1):
            for j in range(1, n + 1):
                # If characters are the same, take the diagonal value
                if word1[i - 1] == word2[j - 1]:
                    dp[i][j] = dp[i - 1][j - 1]
                else:
                    # Otherwise, take the min of three possible operations
                    dp[i][j] = 1 + min(dp[i - 1][j],    # Delete
                                       dp[i][j - 1],    # Insert
                                       dp[i - 1][j - 1]) # Replace

        # Return the value in the bottom-right corner of the dp array
        return dp[m][n]
