class Solution:
    def gcdOfStrings(self, str1: str, str2: str) -> str:
        def gcd(a, b):
            while b:
                a, b = b, a % b
            return a

        def divides(s, t):
            if len(s) % len(t) != 0:
                return False
            if s.replace(t, "") == "":
                return True
            return False

        def gcdStr(s1, s2):
            gcd_length = gcd(len(s1), len(s2))
            potential_gcd_string = s1[:gcd_length]
            if divides(s1, potential_gcd_string) and divides(s2, potential_gcd_string):
                return potential_gcd_string
            return ""

        return gcdStr(str1, str2)
