class Solution:
    def isHappy(self, n: int) -> bool:
        def sum_of_squares(number):
            total = 0
            while number > 0:
                digit = number % 10
                total += digit * digit
                number //= 10
            return total

        seen_numbers = set()

        while n != 1 and n not in seen_numbers:
            seen_numbers.add(n)
            n = sum_of_squares(n)

        return n == 1
