from typing import List

class Solution:
    def judgePoint24(self, cards: List[int]) -> bool:
        def helper(nums):
            if len(nums) == 1:
                return abs(nums[0] - 24) < 1e-6

            n = len(nums)
            for i in range(n):
                for j in range(n):
                    if i == j:
                        continue

                    a, b = nums[i], nums[j]
                    remaining = [nums[k] for k in range(n) if k != i and k != j]

                    for op in ['+', '-', '*', '/']:
                        if op == '+':
                            new_val = a + b
                        elif op == '-':
                            new_val = a - b
                        elif op == '*':
                            new_val = a * b
                        elif op == '/':
                            if abs(b) < 1e-6:
                                continue
                            new_val = a / b

                        if helper(remaining + [new_val]):
                            return True

            return False

        return helper([float(x) for x in cards])

