from typing import List
import heapq

class Solution:
    def isPossible(self, target: List[int]) -> bool:
        if len(target) == 1:
            return target[0] == 1

        total = sum(target)
        heap = [-x for x in target]
        heapq.heapify(heap)

        while True:
            max_val = -heapq.heappop(heap)

            if max_val == 1:
                return True

            sum_rest = total - max_val

            if sum_rest < 1 or sum_rest >= max_val:
                return False

            if sum_rest == 1:
                return True

            prev = max_val % sum_rest

            if prev == 0:
                prev = sum_rest

            if prev < 1:
                return False

            total = sum_rest + prev
            heapq.heappush(heap, -prev)

