class Solution:
    def uniqueLetterString(self, s: str) -> int:
        n = len(s)
        last_pos = {}
        result = 0

        for i in range(n):
            char = s[i]

            left = last_pos.get(char, [-1])[-1]

            right = n
            for j in range(i + 1, n):
                if s[j] == char:
                    right = j
                    break

            contribution = (i - left) * (right - i)
            result += contribution

            if char not in last_pos:
                last_pos[char] = []
            last_pos[char].append(i)

        return result

