class Solution:
    def isSubsequence(self, s: str, t: str) -> bool:
        if len(s) == 0:
            return True
        if len(t) == 0:
            return False

        s_idx = 0

        for char in t:
            if char == s[s_idx]:
                s_idx += 1
                if s_idx == len(s):
                    return True

        return False

