class Solution:
    def longestDupSubstring(self, s: str) -> str:
        n = len(s)

        def search(L):
            if L == 0:
                return -1

            base = 26
            mod = 2**63 - 1

            h = 0
            for i in range(L):
                h = (h * base + ord(s[i]) - ord('a')) % mod

            seen = {h}
            base_L = pow(base, L, mod)

            for start in range(1, n - L + 1):
                h = (h * base - (ord(s[start - 1]) - ord('a')) * base_L + ord(s[start + L - 1]) - ord('a')) % mod

                if h in seen:
                    return start
                seen.add(h)

            return -1

        left, right = 0, n
        result_pos = -1
        result_len = 0

        while left < right:
            mid = (left + right) // 2
            pos = search(mid)

            if pos != -1:
                result_pos = pos
                result_len = mid
                left = mid + 1
            else:
                right = mid

        if result_pos == -1:
            return ""
        return s[result_pos:result_pos + result_len]

