    def removeStones(self, stones: List[List[int]]) -> int:
        parent = {}

        def find(x):
            if x not in parent:
                parent[x] = x
            if parent[x] != x:
                parent[x] = find(parent[x])
            return parent[x]

        def union(x, y):
            root_x = find(x)
            root_y = find(y)
            if root_x != root_y:
                parent[root_x] = root_y

        for x, y in stones:
            union(x, ~y)

        unique_roots = set()
        for x, y in stones:
            unique_roots.add(find(x))

        return len(stones) - len(unique_roots)

