from collections import deque

class Solution:
    def constrainedSubsetSum(self, nums: List[int], k: int) -> int:
        n = len(nums)
        dp = [0] * n
        dq = deque()
        res = float('-inf')
        for i in range(n):
            while dq and dq[0] < i - k:
                dq.popleft()
            prev = 0
            if dq:
                prev = max(prev, dp[dq[0]])
            dp[i] = nums[i] + prev
            while dq and dp[dq[-1]] <= dp[i]:
                dq.pop()
            dq.append(i)
            res = max(res, dp[i])
        return res

