class Solution:
    def smallerNumbersThanCurrent(self, nums: List[int]) -> List[int]:
        sorted_nums = sorted(nums)
        count_map = {}
        result = []

        for idx, num in enumerate(sorted_nums):
            if num not in count_map:
                count_map[num] = idx

        for num in nums:
            result.append(count_map[num])

        return result
