        n = len(nums)
        pairs = sorted(zip(nums, cost), key=lambda x: x[0])
        total_weight = sum(cost)
        cum_weight = 0
        target = pairs[0][0]
        for num, c in pairs:
            cum_weight += c
            if cum_weight >= total_weight / 2:
                target = num
                break

        total_cost = 0
        for i in range(n):
            total_cost += abs(nums[i] - target) * cost[i]
        return total_cost
