class Solution:
    def minOperations(self, nums: List[int], x: int) -> int:
        total = sum(nums)
        target = total - x
        if target < 0:
            return -1
        if target == 0:
            return len(nums)

        left = curr = max_len = 0
        n = len(nums)
        for right in range(n):
            curr += nums[right]
            while curr > target:
                curr -= nums[left]
                left += 1
            if curr == target:
                max_len = max(max_len, right - left + 1)

        return n - max_len if max_len > 0 else -1
