class Solution:
    def removeStones(self, stones: List[List[int]]) -> int:
        parent = {}

        def find(x):
            if x != parent[x]:
                parent[x] = find(parent[x])
            return parent[x]

        def union(x, y):
            rootX = find(x)
            rootY = find(y)
            if rootX != rootY:
                parent[rootY] = rootX

        for stone in stones:
            x, y = stone
            row = f"r{x}"
            col = f"c{y}"
            if row not in parent:
                parent[row] = row
            if col not in parent:
                parent[col] = col
            union(row, col)

        roots = set()
        for key in parent:
            roots.add(find(key))

        return len(stones) - len(roots)
