class Solution:
    def numberOfSubstrings(self, s: str) -> int:
        count = 0
        last_occurrence = [-1, -1, -1]
        for i, char in enumerate(s):
            if char == 'a':
                last_occurrence[0] = i
            elif char == 'b':
                last_occurrence[1] = i
            else:
                last_occurrence[2] = i

            if last_occurrence[0] != -1 and last_occurrence[1] != -1 and last_occurrence[2] != -1:
                min_index = min(last_occurrence)
                count += min_index + 1
        return count
