class Solution:
    def minCost(self, houses: list, cost: list, m: int, n: int, target: int) -> int:
        dp = [[[float('inf')] * (target + 1) for _ in range(n + 1)] for __ in range(m)]

        if houses[0] == 0:
            for color in range(1, n + 1):
                dp[0][color][1] = cost[0][color - 1]
        else:
            dp[0][houses[0]][1] = 0

        for i in range(1, m):
            if houses[i] == 0:
                for color_cur in range(1, n + 1):
                    for color_prev in range(1, n + 1):
                        for t in range(1, target + 1):
                            if color_cur == color_prev:
                                new_t = t
                            else:
                                new_t = t + 1
                            if new_t <= target:
                                dp[i][color_cur][new_t] = min(
                                    dp[i][color_cur][new_t],
                                    dp[i-1][color_prev][t] + cost[i][color_cur - 1]
                                )
            else:
                color_cur = houses[i]
                for color_prev in range(1, n + 1):
                    for t in range(1, target + 1):
                        if color_cur == color_prev:
                            new_t = t
                        else:
                            new_t = t + 1
                        if new_t <= target:
                            dp[i][color_cur][new_t] = min(
                                dp[i][color_cur][new_t],
                                dp[i-1][color_prev][t]
                            )

        res = min(dp[m-1][color][target] for color in range(1, n+1))
        return res if res != float('inf') else -1
