class Solution:
    def palindromePairs(self, words: List[str]) -> List[List[int]]:
        def is_palindrome(s):
            return s == s[::-1]

        word_index = {word: i for i, word in enumerate(words)}
        res = []

        for i, word in enumerate(words):
            n = len(word)
            for j in range(n + 1):
                prefix = word[:j]
                suffix = word[j:]

                if is_palindrome(prefix):
                    reversed_suffix = suffix[::-1]
                    if reversed_suffix != word and reversed_suffix in word_index:
                        res.append([word_index[reversed_suffix], i])

                if j != n and is_palindrome(suffix):
                    reversed_prefix = prefix[::-1]
                    if reversed_prefix != word and reversed_prefix in word_index:
                        res.append([i, word_index[reversed_prefix]])

        return res
