from collections import deque

class Solution:
    def maxSlidingWindow(self, nums: list, k: int) -> list:
        dq = deque()
        result = []
        n = len(nums)
        for i in range(n):
            if dq and dq[0] == i - k:
                dq.popleft()
            while dq and nums[dq[-1]] <= nums[i]:
                dq.pop()
            dq.append(i)
            if i >= k - 1:
                result.append(nums[dq[0]])
        return result

