class Solution:
    def smallestSufficientTeam(self, req_skills: List[str], people: List[List[str]]) -> List[int]:
        skill_to_index = {skill: i for i, skill in enumerate(req_skills)}
        n_skills = len(req_skills)
        n_people = len(people)

        people_skills_mask = [0] * n_people
        for i, person_skills in enumerate(people):
            mask = 0
            for skill in person_skills:
                if skill in skill_to_index:
                    mask |= 1 << skill_to_index[skill]
            people_skills_mask[i] = mask

        dp = [None] * (1 << n_skills)
        dp[0] = []

        for i in range(n_people):
            person_mask = people_skills_mask[i]
            for mask in range(len(dp) - 1, -1, -1):
                if dp[mask] is None:
                    continue
                new_mask = mask | person_mask
                if dp[new_mask] is None or len(dp[new_mask]) > len(dp[mask]) + 1:
                    dp[new_mask] = dp[mask] + [i]

        return dp[(1 << n_skills) - 1]
