class Solution:
    def countArrangement(self, n: int) -> int:
        memo = {}

        def solve(mask):
            if mask in memo:
                return memo[mask]

            num_placed = bin(mask).count('1')

            if num_placed == n:
                return 1

            pos = num_placed + 1
            count = 0

            for num in range(1, n + 1):
                if not (mask & (1 << (num - 1))):
                    if (num % pos == 0) or (pos % num == 0):
                        count += solve(mask | (1 << (num - 1)))

            memo[mask] = count
            return count

        return solve(0)

