from typing import List

class Solution:
    def canPlaceFlowers(self, flowerbed: List[int], n: int) -> bool:
        if n == 0:
            return True

        count = 0
        for i in range(len(flowerbed)):
            if flowerbed[i] == 0:
                # Check if the left plot is empty (or it's the beginning of the bed)
                empty_left = (i == 0) or (flowerbed[i-1] == 0)

                # Check if the right plot is empty (or it's the end of the bed)
                empty_right = (i == len(flowerbed) - 1) or (flowerbed[i+1] == 0)

                if empty_left and empty_right:
                    # Plant a flower
                    flowerbed[i] = 1
                    count += 1

                    # If we have planted enough flowers, we can return true
                    if count >= n:
                        return True

        # If we finish the loop, it means we couldn't plant n flowers
        return False
