from typing import List

class Solution:
    def candy(self, ratings: List[int]) -> int:
        n = len(ratings)

        candies = [1] * n

        # First pass: from left to right
        # This ensures that any child with a higher rating than their left neighbor
        # gets more candies than that neighbor.
        for i in range(1, n):
            if ratings[i] > ratings[i-1]:
                candies[i] = candies[i-1] + 1

        # Second pass: from right to left
        # This pass handles the right neighbor condition. If a child has a higher
        # rating than their right neighbor, they must have more candies. We take
        # the maximum of the current candy count (which already satisfies the left
        # neighbor condition) and the amount required by the right neighbor.
        # This ensures both conditions are met with the minimum number of candies.
        for i in range(n - 2, -1, -1):
            if ratings[i] > ratings[i+1]:
                candies[i] = max(candies[i], candies[i+1] + 1)

        return sum(candies)
