class Solution:
    def maximumSwap(self, num: int) -> int:
        s = list(str(num))
        n = len(s)

        last_indices = {int(s[i]): i for i in range(n)}

        for i in range(n):
            current_digit = int(s[i])
            for d in range(9, current_digit, -1):
                if d in last_indices and last_indices[d] > i:
                    j = last_indices[d]
                    s[i], s[j] = s[j], s[i]
                    return int("".join(s))

        return num
