import collections
from typing import List

class Solution:
    def minMutation(self, startGene: str, endGene: str, bank: List[str]) -> int:
        if startGene == endGene:
            return 0

        bank_set = set(bank)
        if endGene not in bank_set:
            return -1

        queue = collections.deque([(startGene, 0)])
        visited = {startGene}

        while queue:
            current_gene, num_mutations = queue.popleft()

            if current_gene == endGene:
                return num_mutations

            for potential_next_gene in bank_set:
                if potential_next_gene not in visited:
                    # Check if the potential next gene is a single mutation away.
                    # sum(generator) is a concise way to count differences.
                    if sum(c1 != c2 for c1, c2 in zip(current_gene, potential_next_gene)) == 1:
                        visited.add(potential_next_gene)
                        queue.append((potential_next_gene, num_mutations + 1))

        return -1

