class Solution:
    def parseBoolExpr(self, expression: str) -> bool:
        stack = []
        for char in expression:
            if char == ')':
                operands = []
                while isinstance(stack[-1], bool):
                    operands.append(stack.pop())

                op = stack.pop()

                if op == '!':
                    stack.append(not operands[0])
                elif op == '&':
                    stack.append(all(operands))
                elif op == '|':
                    stack.append(any(operands))
            elif char in '(,':
                continue
            else:
                if char == 't':
                    stack.append(True)
                elif char == 'f':
                    stack.append(False)
                else:
                    stack.append(char)

        return stack.pop()
