class Solution:
    def isMatch(self, s: str, p: str) -> bool:
        memo = {}

        def dp(i, j):
            if (i, j) in memo:
                return memo[(i, j)]

            if j == len(p):
                return i == len(s)

            first_match = i < len(s) and (p[j] == s[i] or p[j] == '.')

            if j + 1 < len(p) and p[j + 1] == '*':
                # Two cases for 'x*':
                # 1. Match zero occurrences of 'x'. We move past 'x*' in the pattern.
                # 2. Match one or more occurrences of 'x'. This requires a first_match.
                #    We then move to the next char in s, but stay at the same pattern
                #    position j to allow for multiple matches by '*'.
                ans = dp(i, j + 2) or (first_match and dp(i + 1, j))
            else:
                # No '*' involved. A simple match is required.
                # Both pointers must advance if there is a match.
                ans = first_match and dp(i + 1, j + 1)

            memo[(i, j)] = ans
            return ans

        return dp(0, 0)
