class Solution:
    def calculate(self, s: str) -> int:
        n = len(s)
        if n == 0:
            return 0

        res = 0
        last_num = 0
        num = 0
        op = '+'

        for i, ch in enumerate(s):
            if ch.isdigit():
                num = num * 10 + ord(ch) - ord('0')
            if (not ch.isdigit() and ch != ' ') or i == n - 1:
                if op == '+':
                    res += last_num
                    last_num = num
                elif op == '-':
                    res += last_num
                    last_num = -num
                elif op == '*':
                    last_num = last_num * num
                elif op == '/':
                    # truncate toward zero
                    if last_num < 0:
                        last_num = - (abs(last_num) // num)
                    else:
                        last_num = last_num // num
                op = ch
                num = 0

        res += last_num
        return res

