from typing import List

class Solution:
    def coinChange(self, coins: List[int], amount: int) -> int:
        if amount == 0:
            return 0
        max_val = amount + 1
        dp = [max_val] * (amount + 1)
        dp[0] = 0
        for coin in coins:
            if coin > amount:
                continue
            for x in range(coin, amount + 1):
                if dp[x - coin] + 1 < dp[x]:
                    dp[x] = dp[x - coin] + 1
        return dp[amount] if dp[amount] != max_val else -1

