from typing import List, Set

class Solution:
    def findAllConcatenatedWordsInADict(self, words: List[str]) -> List[str]:
        words.sort(key=len)
        word_set: Set[str] = set()
        result: List[str] = []

        for word in words:
            if not word:
                continue
            if self.canForm(word, word_set):
                result.append(word)
            word_set.add(word)

        return result

    def canForm(self, word: str, word_set: Set[str]) -> bool:
        if not word_set:
            return False
        n = len(word)
        dp = [False] * (n + 1)
        dp[0] = True

        for i in range(1, n + 1):
            for j in range(i):
                if not dp[j]:
                    continue
                if word[j:i] in word_set:
                    dp[i] = True
                    break

        return dp[n]

