class Solution:
    def countBinarySubstrings(self, s: str) -> int:
        prev_group_len = 0
        curr_group_len = 1
        result = 0

        for i in range(1, len(s)):
            if s[i] == s[i - 1]:
                curr_group_len += 1
            else:
                result += min(prev_group_len, curr_group_len)
                prev_group_len = curr_group_len
                curr_group_len = 1

        result += min(prev_group_len, curr_group_len)
        return result

