class Solution:
    def uniqueLetterString(self, s: str) -> int:
        n = len(s)
        # For each character, track last two occurrences
        last = [-1] * 26       # last occurrence
        prev = [-1] * 26       # second last occurrence
        res = 0

        for i, ch in enumerate(s):
            idx = ord(ch) - 65  # 'A' -> 0
            if last[idx] != -1:
                res += (last[idx] - prev[idx]) * (i - last[idx])
            prev[idx] = last[idx]
            last[idx] = i

        for idx in range(26):
            if last[idx] != -1:
                res += (last[idx] - prev[idx]) * (n - last[idx])

        return res

