from typing import List
from bisect import bisect_right

class Solution:
    def longestObstacleCourseAtEachPosition(self, obstacles: List[int]) -> List[int]:
        tails = []
        ans = []

        for h in obstacles:
            idx = bisect_right(tails, h)
            if idx == len(tails):
                tails.append(h)
            else:
                tails[idx] = h
            ans.append(idx + 1)

        return ans

