class Solution:
    def numberToWords(self, num: int) -> str:
        if num == 0:
            return "Zero"

        below_20 = ["", "One", "Two", "Three", "Four", "Five", "Six", "Seven",
                    "Eight", "Nine", "Ten", "Eleven", "Twelve", "Thirteen",
                    "Fourteen", "Fifteen", "Sixteen", "Seventeen", "Eighteen",
                    "Nineteen"]

        tens = ["", "", "Twenty", "Thirty", "Forty", "Fifty", "Sixty",
                "Seventy", "Eighty", "Ninety"]

        thousands = ["", "Thousand", "Million", "Billion"]

        def three_digit_to_words(n: int) -> str:
            res = []
            if n >= 100:
                res.append(below_20[n // 100])
                res.append("Hundred")
                n %= 100
            if n >= 20:
                res.append(tens[n // 10])
                n %= 10
            if 0 < n < 20:
                res.append(below_20[n])
            return " ".join(res)

        res_parts = []
        i = 0
        while num > 0:
            part = num % 1000
            if part != 0:
                words = three_digit_to_words(part)
                if thousands[i]:
                    words = words + " " + thousands[i]
                res_parts.append(words)
            num //= 1000
            i += 1

        return " ".join(reversed(res_parts))

