from typing import List

class Solution:
    def lastStoneWeightII(self, stones: List[int]) -> int:
        total = sum(stones)
        target = total // 2
        dp = [False] * (target + 1)
        dp[0] = True
        for w in stones:
            for j in range(target, w - 1, -1):
                if dp[j - w]:
                    dp[j] = True
        for j in range(target, -1, -1):
            if dp[j]:
                return total - 2 * j
        return 0

