from typing import List

class Solution:
    def maximumScore(self, nums: List[int], k: int) -> int:
        n = len(nums)
        left = right = k
        curr_min = nums[k]
        res = curr_min

        while left > 0 or right < n - 1:
            if left == 0:
                right += 1
            elif right == n - 1:
                left -= 1
            elif nums[left - 1] < nums[right + 1]:
                right += 1
            else:
                left -= 1

            curr_min = min(curr_min, nums[left], nums[right])
            res = max(res, curr_min * (right - left + 1))

        return res

