from typing import List

class Solution:
    def maxScoreWords(self, words: List[str], letters: List[str], score: List[int]) -> int:
        n = len(words)
        freq = [0] * 26
        for ch in letters:
            freq[ord(ch) - ord('a')] += 1

        word_counts = []
        word_scores = []
        for w in words:
            cnt = [0] * 26
            s = 0
            for ch in w:
                idx = ord(ch) - ord('a')
                cnt[idx] += 1
                s += score[idx]
            word_counts.append(cnt)
            word_scores.append(s)

        self.best = 0

        def backtrack(i: int, cur_score: int):
            if i == n:
                if cur_score > self.best:
                    self.best = cur_score
                return

            backtrack(i + 1, cur_score)

            can_take = True
            cnt = word_counts[i]
            for c in range(26):
                if cnt[c] > freq[c]:
                    can_take = False
                    break

            if can_take:
                for c in range(26):
                    freq[c] -= cnt[c]
                backtrack(i + 1, cur_score + word_scores[i])
                for c in range(26):
                    freq[c] += cnt[c]

        backtrack(0, 0)
        return self.best

