from typing import List

class Solution:
    def removeStones(self, stones: List[List[int]]) -> int:
        n = len(stones)

        parent = list(range(n))
        rank = [0] * n

        def find(x):
            while parent[x] != x:
                parent[x] = parent[parent[x]]
                x = parent[x]
            return x

        def union(x, y):
            rx, ry = find(x), find(y)
            if rx == ry:
                return
            if rank[rx] < rank[ry]:
                parent[rx] = ry
            elif rank[rx] > rank[ry]:
                parent[ry] = rx
            else:
                parent[ry] = rx
                rank[rx] += 1

        for i in range(n):
            x1, y1 = stones[i]
            for j in range(i + 1, n):
                x2, y2 = stones[j]
                if x1 == x2 or y1 == y2:
                    union(i, j)

        roots = set(find(i) for i in range(n))
        return n - len(roots)

