from typing import List

class Solution:
    def minCost(self, houses: List[int], cost: List[List[int]], m: int, n: int, target: int) -> int:
        INF = 10**18

        # dp[i][k][c]: min cost to paint up to house i (0-based),
        # with k neighborhoods, where house i has color c (0-based)
        dp = [[[INF] * n for _ in range(target + 1)] for _ in range(m)]

        # Initialize for first house
        if houses[0] != 0:
            c = houses[0] - 1
            dp[0][1][c] = 0
        else:
            for c in range(n):
                dp[0][1][c] = cost[0][c]

        # Fill DP
        for i in range(1, m):
            for k in range(1, min(target, i + 1) + 1):
                if houses[i] != 0:
                    c = houses[i] - 1
                    best = INF
                    # same color as previous
                    if dp[i - 1][k][c] < best:
                        best = dp[i - 1][k][c]
                    # different color from previous
                    if k > 1:
                        for pc in range(n):
                            if pc == c:
                                continue
                            if dp[i - 1][k - 1][pc] < best:
                                best = dp[i - 1][k - 1][pc]
                    if best < INF:
                        dp[i][k][c] = min(dp[i][k][c], best)
                else:
                    for c in range(n):
                        best = INF
                        # same color as previous
                        if dp[i - 1][k][c] < best:
                            best = dp[i - 1][k][c]
                        # different color from previous
                        if k > 1:
                            for pc in range(n):
                                if pc == c:
                                    continue
                                if dp[i - 1][k - 1][pc] < best:
                                    best = dp[i - 1][k - 1][pc]
                        if best < INF:
                            dp[i][k][c] = min(dp[i][k][c], best + cost[i][c])

        # Result
        ans = min(dp[m - 1][target])
        return -1 if ans == INF else ans

