from typing import List

class Solution:
    def palindromePairs(self, words: List[str]) -> List[List[int]]:
        word_to_index = {w: i for i, w in enumerate(words)}
        res = []

        def is_pal(s: str, l: int, r: int) -> bool:
            while l < r:
                if s[l] != s[r]:
                    return False
                l += 1
                r -= 1
            return True

        empty_index = word_to_index.get("")
        if empty_index is not None:
            for i, w in enumerate(words):
                if i == empty_index:
                    continue
                if w == w[::-1]:
                    res.append([empty_index, i])
                    res.append([i, empty_index])

        for i, word in enumerate(words):
            rev_word = word[::-1]
            j = word_to_index.get(rev_word)
            if j is not None and j != i:
                res.append([i, j])

        for i, word in enumerate(words):
            n = len(word)
            for cut in range(1, n):
                if is_pal(word, 0, cut - 1):
                    rev_right = word[cut:][::-1]
                    j = word_to_index.get(rev_right)
                    if j is not None and j != i:
                        res.append([j, i])
                if is_pal(word, cut, n - 1):
                    rev_left = word[:cut][::-1]
                    j = word_to_index.get(rev_left)
                    if j is not None and j != i:
                        res.append([i, j])

        return res

