class Solution:
    def checkInclusion(self, s1: str, s2: str) -> bool:
        len1, len2 = len(s1), len(s2)
        if len1 > len2:
            return False

        count1 = [0] * 26
        window = [0] * 26
        base = ord('a')

        for c in s1:
            count1[ord(c) - base] += 1
        for c in s2[:len1]:
            window[ord(c) - base] += 1

        if count1 == window:
            return True

        for i in range(len1, len2):
            window[ord(s2[i]) - base] += 1
            window[ord(s2[i - len1]) - base] -= 1
            if count1 == window:
                return True

        return False

