class Solution:
    def getPermutation(self, n: int, k: int) -> str:
        factorial = [1] * (n + 1)
        for i in range(1, n + 1):
            factorial[i] = factorial[i - 1] * i

        k -= 1  # convert to 0-based index
        nums = [str(i) for i in range(1, n + 1)]
        result = []

        for i in range(n, 0, -1):
            idx = k // factorial[i - 1]
            k %= factorial[i - 1]
            result.append(nums.pop(idx))

        return "".join(result)

